mapper.update:
UPDATE blog_article_tag
SET tag_id = #{dest.id}
WHERE tag_id = #{src.id}

mapper.select:
SELECT COUNT(DISTINCT(bat.tag_id)) AS counts FROM blog_article_tag bat LEFT OUTER JOIN blog_article ba ON bat.article_id = ba.id LEFT OUTER JOIN blog_space bs ON ba.space_id = bs.id WHERE ba.art_status = 0
AND ba.space_id = ${space.id}
AND ba.isPrivate = FALSE AND bs.is_private = FALSE
